mapper.insert:
insert into food_comment (food_id, campus_id,comment, date,
grade, phone, tag
)
values (#{foodId,jdbcType=BIGINT}, #{campusId,jdbcType=INTEGER},#{comment,jdbcType=VARCHAR},
#{date,jdbcType=DATE},
#{grade,jdbcType=SMALLINT}, #{phone,jdbcType=VARCHAR}, #{tag,jdbcType=SMALLINT}
)

mapper.select:
SELECT food.food_id,food.campus_id, name, COMMENT, date, food_comment.grade, nickname FROM food_comment, users, food
food_comment.phone = users.phone and food_comment.food_id=food.food_id and food.campus_id=food_comment.campus_id and comment is not null and comment !="" and food.campus_id=#{campusId,jdbcType=INTEGER}
and food_id = #{search,jdbcType=INTEGER})
order by ${sort}
${order}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

SELECT count(*) FROM food_comment, users, food
food_comment.phone = users.phone and food.campus_id=food_comment.campus_id and food_comment.food_id=food.food_id and comment is not null and comment !="" and food.campus_id=#{campusId,jdbcType=INTEGER}
and food_id = #{search,jdbcType=VARCHAR})

mapper.delete:
delete
from 
 food_comment

   food_id=#{foodId,jdbcType=BIGINT}
   and date_format(date,'%Y-%m-%d')=#{date,jdbcType=DATE}
   and grade=#{grade} and campus_id=#{campusId,jdbcType=INTEGER}
