mapper.select:
select food.food_id, food.campus_id,name, price, discount_price, img_url, is_discount,food_count, sale_number,info,message,food.grade,is_full_discount from food
food.tag=1 and food.campus_id=#{campusId,jdbcType=INTEGER}
and category_id = #{categoryId,jdbcType=BIGINT}
AND (food_flag like #{tagPattern} OR name like #{tagPattern})
ORDER BY sale_number DESC
ORDER BY case when is_discount=1 then discount_price else price END ASC
ORDER BY modify_time DESC
LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}

	
	
	select
    food.food_id, food.campus_id,name, price, discount_price, img_url,
	is_discount,food_count,
	sale_number,info,message,food.grade
	from food,food_comment
	
		food.tag=1 and food.campus_id=#{campusId,jdbcType=INTEGER}
		and food.food_id=food_comment.food_id 
		and food.campus_id=food_comment.campus_id
		
			and category_id = #{categoryId,jdbcType=BIGINT}
		
		
			and ( ( food_flag LIKE #{oneFlagPattern} OR
			food_flag LIKE
			#{twoFlagPattern}
			) OR ( name LIKE
			#{oneFlagPattern} OR name LIKE
			#{twoFlagPattern}
			) )
		
	
	group by food.food_id
	
		
			ORDER BY sale_number DESC
		
		 ORDER BY price ASC
		
		
			ORDER BY modify_time DESC
		
	
	
		LIMIT #{limit,jdbcType=INTEGER} OFFSET
		#{offset,jdbcType=INTEGER}

select
from food where is_discount = 1 and tag=1 and campus_id=#{campusId,jdbcType=INTEGER} order by modify_time DESC,discount_price ASC
LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}

select
from food where tag=1 and campus_id=#{campusId,jdbcType=INTEGER} order by modify_time DESC
LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}

select
from food where tag=1 and campus_id=#{campusId,jdbcType=INTEGER} order by sale_number DESC
LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}

select
from food where to_home=1 and campus_id=#{campusId,jdbcType=INTEGER} and tag=1 and home_image is not null
LIMIT #{limit,jdbcType=INTEGER} OFFSET #{offset,jdbcType=INTEGER}

mapper.update:
update food set
   to_home=#{toHome, jdbcType=SMALLINT}

, home_image=#{homeImage, jdbcType=VARCHAR}

where food_id=#{foodId, jdbcType=BIGINT} and campus_id=#{campusId,jdbcType=INTEGER}

update food set 

	info=#{info, jdbcType=VARCHAR}

where food_id=#{foodId, jdbcType=BIGINT} and campus_id=#{campusId,jdbcType=INTEGER}

update food set	
	to_home=#{toHome, jdbcType=SMALLINT}
where food_id=#{foodId, jdbcType=BIGINT} and campus_id=#{campusId,jdbcType=INTEGER}

mapper.select:
SELECT orders.food_id,food.name as food_name,SUM(orders.order_count) as sales from orders,food

  orders.food_id=food.food_id and orders.campus_id=#{campusId,jdbcType=INTEGER}

	AND together_date BETWEEN #{dateStart,jdbcType=TIMESTAMP}


	AND #{dateEnd,jdbcType=TIMESTAMP}


GROUP BY food_id ORDER BY SUM(order_count) DESC

limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

mapper.update:
UPDATE food
SET food_count = food_count + #{addCount,jdbcType=INTEGER}

campus_id=#{campusId,jdbcType=INTEGER}

and food_id=#{foodId,jdbcType=BIGINT}
