mapper.select:
select

from receiver
where rank = #{rank,jdbcType=VARCHAR}
and phone_id = #{phoneId,jdbcType=VARCHAR}
and is_out=0

mapper.update:
update receiver
set is_out=1
where rank = #{rank,jdbcType=VARCHAR}
and phone_id = #{phoneId,jdbcType=VARCHAR}

update receiver
set phone = #{phone,jdbcType=VARCHAR},
name = #{name,jdbcType=VARCHAR},
address = #{address,jdbcType=VARCHAR},
tag = #{tag,jdbcType=SMALLINT}
where rank = #{rank,jdbcType=VARCHAR}
and phone_id = #{phoneId,jdbcType=VARCHAR}
